import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";
import "src./styles.css";


const schema = yup.object().shape({
  email: yup.string().email("Invalid email").required("Email is required"),
  password: yup.string().min(6, "Password must be at least 6 characters").required("Password is required"),
});

const LoginForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const navigate = useNavigate();

  const onSubmit = (data) => {
    console.log("Login Data:", data);
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900">
      <div className="bg-blue-800 p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-white text-2xl font-bold text-center mb-4">Sign In</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4">
            <input
              type="email"
              placeholder="Email"
              {...register("email")}
              className="w-full p-3 rounded bg-gray-100 text-black"
            />
            <p className="text-red-500 text-sm">{errors.email?.message}</p>
          </div>
          <div className="mb-4">
            <input
              type="password"
              placeholder="Password"
              {...register("password")}
              className="w-full p-3 rounded bg-gray-100 text-black"
            />
            <p className="text-red-500 text-sm">{errors.password?.message}</p>
          </div>
          <div className="text-right text-white text-sm mb-4">
            <button type="button" onClick={() => navigate("/forgot-password")} className="hover:underline">
              Forgot your password?
            </button>
          </div>
          <button type="submit" className="w-full bg-white text-blue-800 p-3 rounded font-bold">
            Sign in
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
