import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";

const schema = yup.object().shape({
  fullName: yup.string().required("Full Name is required"),
  email: yup.string().email("Invalid email").required("Email is required"),
  password: yup.string().min(6, "Password must be at least 6 characters").required("Password is required"),
});

const SignupForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const navigate = useNavigate();

  const onSubmit = (data) => {
    console.log("Signup Data:", data);
    navigate("/");
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900">
      <div className="bg-blue-800 p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-white text-2xl font-bold text-center mb-4">Sign Up</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4">
            <input
              type="text"
              placeholder="Full Name"
              {...register("fullName")}
              className="w-full p-3 rounded bg-gray-100 text-black"
            />
            <p className="text-red-500 text-sm">{errors.fullName?.message}</p>
          </div>
          <div className="mb-4">
            <input
              type="email"
              placeholder="Email"
              {...register("email")}
              className="w-full p-3 rounded bg-gray-100 text-black"
            />
            <p className="text-red-500 text-sm">{errors.email?.message}</p>
          </div>
          <div className="mb-4">
            <input
              type="password"
              placeholder="Password"
              {...register("password")}
              className="w-full p-3 rounded bg-gray-100 text-black"
            />
            <p className="text-red-500 text-sm">{errors.password?.message}</p>
          </div>
          <button type="submit" className="w-full bg-white text-blue-800 p-3 rounded font-bold">
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignupForm;
